import styled from "styled-components";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {  postRegister } from "../../services/Services";

export default function SignUp() {
    const [form, setForm] = useState({ name: "", email: "", password: "" });
    const [responseVerification, setResponseVerification] = useState(undefined);
    const [disabled, setDisabled] = useState(false);
    const navigate = useNavigate();

    function handleForm(e) {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    }

    function register(e) {
        e.preventDefault();

        const body = { ...form }

        postRegister(body)
            .then(res => {
                setResponseVerification(res.data)
                navigate("/");
            })
            .catch(err => {
                alert(err.message)
                setDisabled(false)
            })
        if (responseVerification === undefined) {
            return setDisabled(true)
        }
    }

    return (
        <Container>
            <Title>My Wallet</Title>
            <form onSubmit={register}>
                <input
                    name="name"
                    value={form.name}
                    onChange={handleForm}
                    type="text"
                    placeholder="Nome"
                    disabled={disabled}
                    required
                />
                <input
                    name="email"
                    value={form.email}
                    onChange={handleForm}
                    type="text"
                    placeholder="E-mail"
                    disabled={disabled}
                    required
                />
                <input
                    name="password"
                    value={form.password}
                    onChange={handleForm}
                    type="text"
                    placeholder="Senha"
                    disabled={disabled}
                    required
                />
                <input
                    name="password"
                    value={form.password}
                    onChange={handleForm}
                    type="text"
                    placeholder="Confirme a senha"
                    disabled={disabled}
                    required
                />
                <button>Cadastrar</button>
                <StyledLink to={"/"}>JÃ¡ tem uma conta? Entre agora!</StyledLink>
            </form>

        </Container>
    )
}
const Container = styled.div`
height: 200vw;
padding:15%;
background-color: #8C11BE;

input{
margin-left: -5%;
margin-bottom: 4%;
width:38vh;
padding: 15px; 
border:1px solid #D4D4D4;
border-radius: 5px;
&::placeholder{
font-family: 'Lexend Deca', sans-serif;
font-size:15px;
color:#DBDBDB;
    }
}
button{
margin-left: -5%;
margin-bottom:15%;
width:38vh;
padding: 15px;
font-weight:600;
font-size: 15px;
color: #FFFFFF;
background-color:#52B6FF;
border-radius: 5px;
border:none;
}
img{
margin-left: 24px;
}
`
const Title = styled.h1`
display:flex;
justify-content: center;
font-size:15px;
color: #FFFFFF;
font-family: 'Saira Stencil One', cursive;

`
const StyledLink = styled(Link)`
    margin-top:5%;
    margin-left:8%;
    font-size:14px;
    color: #52B6FF;
`
