import { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import UserContext from "../../contexts/UserContext";
import { postNewRegistries } from "../../services/Services";

export default function NewEnter() {
    // const [value, setValue] = useState("");
    // const [description, setDescription] = useState("");
    const [form, setForm] = useState({ value: "", description: "" });
    console.log(form.value, form.description)
    const { userData } = useContext(UserContext);

    const navigate = useNavigate();

    function handleForm(e) {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    }

   async function handleSubmit(e) {
        e.preventDefault();

        const headers = {
            headers: {
                Authorization: `Bearer ${userData.token}`
            }
        };

        // const body = {
        //     value: Number(form.value),
        //     description: form.description,
        //     type: "credit"
        // };
const body = {
    ...form, value: Number(form.value),type: "credit"
}
      try{  await postNewRegistries(body, headers)
            // .then((res) => {
         alert("Sucesso")
                navigate("/registros");
            // })
      }catch(err){ 
        console.log(err)
        alert("Verifique se a entrada está correta!")
    }
   }
    return (
        <Container>
            <Title>Nova entrada</Title>

                <input             
                    name="value"
                    value={form.value}
                    onChange={handleForm}
                    type="number"
                    placeholder="Valor"
                    required
                />
                <input
                    name="description"
                    value={form.description}
                    onChange={handleForm}
                    type="text"
                    placeholder="Descrição"
                    required
                />
                <button  on>Salvar entrada</button>

        </Container>
    )
}
