import dayjs from "dayjs";
import styled from "styled-components";


export default function Registrie({ registries }) {
    return (
        <Container>
            {registries.transactions ? registries.transactions.map(({ date, description, type, value }, i) => (

                    <Box key={i}>
                        <Date>{dayjs(date).format("DD/MM")}</Date>
                        <span>{description}</span>
                        <Value type={type}>{value}</Value>
                    </Box>
                )) : ""}
        </Container>
    )
}

const Container = styled.div`
background-color: #8C11BE;
height: 120vh;
position: fixed;
right:0;
left:0;
top: 0;
`

const Date = styled.p`
 color:grey;
 `
const Box = styled.div`
display: flex;
    justify-content: center;
    align-items: center;
    margin-left: -5%;
    margin-bottom:15%;
    width: 76vw;
    height: 6vh;
    font-weight:600;
    font-size: 15px;
    font-family: 'Raleway', sans-serif;
    color: #FFFFFF;
    background-color:#A328D6;
    border-radius: 5px;
    border:none;
span{
    display:flex;
}
`

const Value = styled.p`
 color: ${props => props.type === "debit" ? "#C70000" : "#03AC00"};
 `
