import styled from "styled-components";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {postLogin} from "../../services/Services";

export default function SignUp() {
    const [fomr, setForm] = useState({ name: "", email: "", password: "" });
    const [responseVerification, setResponseVerification] = useState(undefined);
    const [disabled, setDisabled] = useState(false);
    const navigate = useNavigate();

function handleForm(e){
    const {name, value} = e.target;
    setForm({...form, [name]: value});
}

function register(e){
    e.preventDefault;

    const body = {...form}

    postLogin(body)
    .then(res => {
        setResponseVerification(res.data)
        navigate("/");
    })
    .catch(err =>{
        alert(err.message)
        setDisabled(false)
    })
    if (responseVerification === undefined) {
        return setDisabled(true)
    }
}

    return (
        <Container>
            <Title>My Wallet</Title>
            <form onSubmit={register}>
                <input
                    name="name"
                    value={form.name}
                    onChange={handleForm}
                    type="text"
                    placeholder="Nome"
                    disabled={disabled}
                    required
                />
                <input
                    name="email"
                    value={form.email}
                    onChange={handleForm}
                    type="text"
                    placeholder="E-mail"
                    disabled={disabled}
                    required
                />
                <input
                    name="password"
                    value={form.password}
                    onChange={handleForm}
                    type="text"
                    placeholder="Senha"
                    disabled={disabled}
                    required
                />
                <button>Cadastrar</button>
                <StyledLink to={"/"}>JÃ¡ tem uma conta? Entre agora!</StyledLink>
            </form>

        </Container>
    )
}

const StyledLink = styled(Link)`
    margin-top:5%;
    margin-left:8%;
    font-size:14px;
    color: #52B6FF;
`
