import styled from "styled-components";

export default function Registrie({ registries }) {

    function getBalance() {

        let balance = registries.transactions.map((item) => {
            if (item.type === "credit") {
                return +item.value;
            } else {
                return -item.value;
            }
        }).reduce((previousValue, currentValue) => previousValue + currentValue, 0);

        return balance.toFixed(2).replace("");
    }
    const value = getBalance();

    return (

        <>
            {registries.transactions
                ? registries.transactions.map(({ date, description, type, value }, i) => (
                    <List key={i}>
                        <ListDate>{date}</ListDate>
                        <ListDescription>{description}</ListDescription>
                        <ListValue type={type}>{value}</ListValue>
                    </List>))
                : ""
            }
            <Balance>SALDO : <span>{value}</span></Balance>
        </>
    )
}

const List = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-left: -1%;
    margin-bottom:-8%;
    width: 30vh;
    height: 6vh;
    font-size: 16px;
    background-color:#ffffff;
    border-radius: 5px;
    border:none;
`
const ListDate = styled.div`
display: flex;
flex-direction: column;
justify-content: flex-start;
margin-left: -10%;
`
const ListDescription = styled.span`
`
const Balance = styled.div`
margin-top: 42vh;
margin-left: -25px;
font-size: 18px;
color: #000000;
display: flex;
justify-content: space-between;
`
const ListValue = styled.span`
 color: ${props => props.type === "debit" ? "#C70000" : "#03AC00"};
 margin-right: -55px;
display: flex;
justify-content: space-between;
 `
