import dayjs from "dayjs";
import styled from "styled-components";


export default function Registrie({ registries }) {
    return (
        <Container>

            {registries.transactions ? registries.transactions.map(({ date, description, type, value }, i) => (

                    <Box key={i}>
                        <span>{dayjs(date).format("DD/MM")}</span>
                        <span>{description}</span>
                        <Value type={type}>{value}</Value>
                    </Box>
                )) : ""}
        </Container>
    )
}

const Container = styled.div`
background-color: #8C11BE;
height: 120vh;
position: fixed;
right:0;
left:0;
top: 0;
`
const ListTransactions = styled.div`
display: flex;
justify-content: center;
align-items: center;
height: 62vh;
padding: 10%;
margin-top: 120px;
margin-right: 25px;
margin-left: 25px;
margin-bottom: 50px;
border-radius: 5px;
background-color: #ffffff;
color: #868686;
font-family: Raleway;
fon-size:20px;
span{
    display:flex;
    justify-content: center;
    align-items:center;
}
`
const Box = styled.div`
display: flex;
    justify-content: center;
    align-items: center;
    margin-left: -5%;
    margin-bottom:15%;
    width: 76vw;
    height: 6vh;
    background-color:#fffff;
    border-radius: 5px;
    border:none;
span{
    display:flex;
}
`
const Value = styled.p`
 color: ${props => props.type === "debit" ? "#C70000" : "#03AC00"};
 `
