import { useState, useContext } from "react";
import styled from "styled-components";
import UserContext from "../../contexts/UserContext.js"
import { postNewRegistries } from "../../services/Services.js";
export default function NewEnter() {
    const [form, setForm] = useState({ value: "", description: "" });
    const {userData} = useContext(UserContext)

    function handleForm(e) {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    }

    function handleSubmit(e) {
        e.preventDefault();
        const config ={
            headers: {
                Authorization: `Bearer ${userData.token}`
            }
        };

        const body = {value: Number(form.value), description: form.description, type: "credit"};

        postNewRegistries(config,body)
        .then((res)=>{
            na()
        })
    }
    return (
        <Container>

            <Title>Nova entrada</Title>

            <Form onSubmit={handleSubmit}>
                <input
                    name="value"
                    value={form.value}
                    onChange={handleForm}
                    type="text"
                    placeholder="Valor"
                    required
                />

                <input
                    name="description"
                    value={form.description}
                    onChange={handleForm}
                    type="number"
                    placeholder="Descrição"
                    required
                />
                <StyledButton type="submit">
                    Salvar entrada
                </StyledButton>
            </Form>

        </Container>
    )
}
const Container = styled.div`
    height: 200vw;
    padding:15%;
    background-color: #8C11BE;
`
const Form = styled.form`
    margin-top: 80px;
    input{
    margin-left: -5%;
    margin-bottom: 4%;
    width:38vh;
    padding: 15px; 
    border:1px solid #D4D4D4;
    border-radius: 5px;
    &::placeholder{
    font-family: 'Raleway', sans-serif;
    font-size:16px;
    color:#000000;
        }
    }
    button{
    margin-left: -5%;
    margin-bottom:15%;
    width:38vh;
    padding: 15px;
    font-weight:600;
    font-size: 15px;
    color: #FFFFFF;
    background-color:#A328D6;
    border-radius: 5px;
    border:none;
    }
`
const Title = styled.h1`
font-family: 'Raleway', sans-serif;
font-size: 26px;
color: #ffffff;
font-weight: bold;
`
const StyledButton = styled.button`
background-color: #8C11BE;
font-family: 'Raleway', sans-serif;
font-size: 26px;
color: #ffffff;
font-weight: bold;
`
