import { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import UserContext from "../../contexts/UserContext.js"
import { postNewRegistries } from "../../services/Services";

export default function NewEnter() {
    // const [form, setForm] = useState({ value: "", description: "" });
    const [value, setValue] = useNavigate("");
    const [description, setDescription] = useState("");
    const { userData } = useContext(UserContext);
    const navigate = useNavigate();

    // function handleForm(e) {
    //     const { name, value } = e.target;
    //     setForm({ ...form, [name]: value });
    // }

    // function handleSubmit(e) {
    //     e.preventDefault();
    //     const config = {
    //         headers: {
    //             Authorization: `Bearer ${userData.token}`
    //         }
    //     };

    // const body = { 
    //     value: Number(form.value), 
    //     description: form.description, 
    //     type: "credit" 
    // };
    // const body = {value: Number(value), description: description, type: "credit"};
    //         postNewRegistries(config, body)
    //             .then((res) => {
    //                 console.log(res.data)
    //                 alert("Valor adicionado com sucesso!");
    //                 navigate("/registros");
    //             })
    //             .catch(err => alert("Verifique se a entrada está correta!"))
    //     }

    async function sendingNewValue(e) {
        e.preventDefault();
        const body = { value: Number(value), description: description, type: "credit" };
        const config = {
            headers: {
                Authorization: `Bearer ${userData.token}`
            }
        };
        try {
            postNewRegistries(body, config)

            alert("Valor adicionado com sucesso!");
            navigate("/registros");

        } catch (err) {
            console.log(err)
            alert("Verifique se os dados foram digitados corretamente");
        }
    }

    return (
        <Container>

            <Title>Nova entrada</Title>

            <Form>
                <input
                    name="value"
                    value={value}
                    onChange={(e) => setValue(e.target.value)}
                    type="number"
                    placeholder="Valor"
                    required
                />

                <input
                    name="description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    type="text"
                    placeholder="Descrição"
                    required
                />
                <StyledButton type="submit" onClick={sendingNewValue}>
                    Salvar entrada
                </StyledButton>
            </Form>

        </Container>
    )
}
const Container = styled.div`
    height: 200vw;
    padding:15%;
    background-color: #8C11BE;
`
const Form = styled.form`
    margin-top: 80px;
    input{
    margin-left: -5%;
    margin-bottom: 4%;
    width:38vh;
    padding: 15px; 
    border:1px solid #D4D4D4;
    border-radius: 5px;
    &::placeholder{
    font-family: 'Raleway', sans-serif;
    font-size:16px;
    color:#000000;
        }
    }
    button{
        background-color: #8C11BE;
        font-family: 'Raleway', sans-serif;
        font-size: 26px;
        color: #ffffff;
        font-weight: bold;
    }
`
const Title = styled.h1`
font-family: 'Raleway', sans-serif;
font-size: 26px;
color: #ffffff;
font-weight: bold;
`
const StyledButton = styled.button`
background-color: #8C11BE;
font-family: 'Raleway', sans-serif;
font-size: 26px;
color: #ffffff;
font-weight: bold;
`
