import styled from "styled-components";
import Navbar from "../Navbar/Navbar";
import { IoAddCircleOutline } from "react-icons/io5";
import { IoRemoveCircleOutline } from "react-icons/io5";
import { Link } from "react-router-dom";
import { useContext, useState } from "react";
import UserContext from "../../contexts/UserContext";
import { getRegistries } from "../../services/Services";
import dayjs from "dayjs";
export default function Registries() {

    const [registries, setRegistries] = useState([]);
    const { userData } = useContext(UserContext);


    function getTransactions() {
        const config = {
            headers: {
                Authorization: `Bearer ${userData.token}`
            }
        }
        getRegistries(config)
            .then(res => {
                setRegistries(res.data);
            })
            .catch(err => {
                console.log(err.response)
                // alert("Deu erro! Tente novamente!");
            })
    }
    getTransactions();

    let value = registries.transactions.map((item) =>
        item.type === 'debit' ? -item.value : +item.value).reduce((previousValue, currentValue) => previousValue + currentValue, 0)

    return (
        <>
            <Navbar />
            <Container>
                {/* {registries.length === 0 ? <ListTransactionsEmpty>
                    <span>Não há registros de
                        <br>
                        </br> entrada ou saída</span>
                </ListTransactionsEmpty> :
                    registries.map(({ date, description, type, value }, i) => (
                        <ListTransactions>
                            <Date>{dayjs(date).format('DD/MM')}</Date>
                            <Description>{description}</Description>
                            <Value type={type}>{value.replace(".", ",")}</Value>
                        </ListTransactions>
                    ))
                } */}
                {registries.lenght === 0
                    ?
                    <ListTransactionsEmpty>
                        <span>
                            Não há registros de
                            <br>
                            </br> entrada ou saída
                        </span>
                    </ListTransactionsEmpty>
                    :
                    <ListTransactions>
                        <h1>Minhas transações</h1>
                        registries.map(({Date, descriptio}))
                    </ListTransactions>

                }
                <p>Saldo</p>
                <Rodape>
                    <Link to="/novaentrada">
                        <EnterBox>
                            <span><IoAddCircleOutline color="#ffffff" size="30px" /></span>

                            <p>Nova entrada</p>
                        </EnterBox>
                    </Link>
                    <Link to="/novasaida">
                        <OutBox>
                            <IoRemoveCircleOutline color="#ffffff" size="30px" />
                            <p>Nova<br></br>saída</p>
                        </OutBox>
                    </Link>
                </Rodape>

            </Container>
        </>
    )
}

const Container = styled.div`
