import styled from "styled-components";
import Loading from "../Loading/Loading";
import { IoCloseOutline } from "react-icons/io5";
import UserContext from "../../contexts/UserContext";
import { useContext } from "react";
import { confirmAlert } from "react-confirm-alert";
import { deleteTransactionsCredit } from "../../services/Services";
export default function Registrie({ registries, listRegistries }) {

    const { userData } = useContext(UserContext);

    function getBalance() {

        const balance = registries.transactions.map((item) => {
            deleteTransactions(item.id)
            if (item.type === "credit") {
                return +item.value;
            } else {
                return -item.value;
            }
        }).reduce((previousValue, currentValue) => previousValue + currentValue, 0);
        if (balance === undefined) {
            return <Loading />
        } else {
            return balance?.toFixed(2).replace(".", ",");
        }
    }

    const value = getBalance();

    function deleteTransactions(id) {
        const config = {
            header: {
                Authorization: `Bearer ${userData.token}`
            }
        }
        confirmAlert({
            title: "Confirmação",
            message: "Quer realmente excluir o valor escolhido?",
            buttons: [
                {
                    label: "Sim",
                    onClick: () => deleteTransactionsCredit(id, config)
                        .then(res => {
                            listRegistries()
                        })
                        .catch(err => {
                            alert("Algo deu errado! Tente novamente!")
                        })
                }, {
                    label: "Não"
                }
            ]
        })
    }
    if (registries.transactions.length === 0) {
        return <Loading />
    }
    return (
        <>
            {registries.transactions
                ? registries.transactions.map(({ date, description, type, value }, i) => (
                    <List key={i}>
                        <ListDate>{date}</ListDate>
                        <ListDescription>{description}</ListDescription>
                        <ListValue type={type}>{value?.toFixed(2).replace(".", ",")}</ListValue>
                        <span ><IoCloseOutline color="#6b9080" size="20px" /></span> </List>))
                : ""
            }
            <Balance><p>SALDO : </p><span>{value}</span></Balance>
        </>
    )
}

const List = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom:-8%;
    padding: 12px;
    width: 30vh;
    height: 6vh;
    font-size: 16px;
    background-color:#ffffff;
    border-radius: 5px;
    border:none;
`
const ListDate = styled.div`
display: flex;
flex-direction: column;
justify-content: flex-start;
margin-left: -10%;
color: #6b9080;
`
const ListDescription = styled.span`
display: flex;
justify-content: flex-start;
margin-left: -20%;
color: #000000;
`
const Balance = styled.div`
position:fixed;
top:30vh;
display: flex;
justify-content: space-between;
padding: 12px;
margin-top: 42vh;

font-size: 18px;
color: #000000;

p{
    font-weight: 800;
    margin-right: 22vh;
    margin-left: -28px;
}
span{
    font-weight: 500;
    color:#03AC00;
}
`
const ListValue = styled.span`
color: ${props => props.type === "debit" ? "#C70000" : "#03AC00"};
font-weight: 500;
margin-right: -55px;
display: flex;
justify-content: space-between;
 `
