            <Form onSubmit={handleSubmit}>
                 <input
                    name="value"
                    value={value}
                    onChange={(e) => setValue(e.target.value)}
                    type="number"
                    placeholder="Valor"
                    required
                />

                <input
                    name="description"
                    value={description}
