import dayjs from "dayjs";
import styled from "styled-components";


export default function Registrie({ registries }) {
    return (
        <Container>
            {registries.transactions ? registries.transactions.map(({ date, description, type, value }, i) => (
                <ListTransactions key={i}>
                    <Date>{dayjs(date).format("DD/MM")}</Date>
                    <Description>{description}</Description>
                    <Value type={type}>{value}</Value>
                </ListTransactions>)) : ""}
        </Container>
    )
}

const Container = styled.div`
background-color: #8C11BE;
height: 120vh;
position: fixed;
right:0;
left:0;
top: 0;
`
const ListTransactions = styled.div`

 `
const Date = styled.p`
 color:grey;
 `
const Description = styled.p`
// color:#000000;
 `
const Value = styled.p`
 color: ${props => props.type === "debit" ? "#C70000" : "#03AC00"};
 `
