import dayjs from "dayjs";
import styled from "styled-components";


export default function Registrie({ registries }) {
    return (

<>
            {registries.transactions ? registries.transactions.map(({ date, description, type, value }, i) => (
             <ListTransactions key={i}>

                        <span>{dayjs(date).format("DD/MM")} </span>
                        <span></span>
                        <Value type={type}>{value}</Value>

                    </ListTransactions>)) : ""}
</>
    )
}


const ListTransactions = styled.div`
display: flex;
    justify-content: center;
    align-items: center;
    margin-left: -5%;
    margin-bottom:15%;
    width: 76vw;
    height: 6vh;
    background-color:#ffffff;
    border-radius: 5px;
    border:none;
span{
    display:flex;
}
`
const Box = styled.div`
display: flex;
    justify-content: center;
    align-items: center;
    margin-left: -5%;
    margin-bottom:15%;
    width: 76vw;
    height: 6vh;
    background-color:#fffff;
    border-radius: 5px;
    border:none;
span{
    display:flex;
}
`
const Value = styled.p`
 color: ${props => props.type === "debit" ? "#C70000" : "#03AC00"};
 `
