import dayjs from "dayjs";
import styled from "styled-components";


export default function Registrie({ registries }) {
    return (
        <Container>
            {registries.transactions ? registries.transactions.map(({ date, description, type, value }, i) => (
                <ListTransactions key={i}>
                    <Box>
                        <Date>{dayjs(date).format("DD/MM")}</Date>
                        <span>{description}</span>
                        <Value type={type}>{value}</Value>
                    </Box>
                </ListTransactions>)) : ""}
        </Container>
    )
}

const Container = styled.div`
background-color: #8C11BE;
height: 120vh;
position: fixed;
right:0;
left:0;
top: 0;
`
const ListTransactions = styled.div`
 display: flex;
 justify-content: center;
 align-items: center;
 height: 62vh;
 padding: 10%;
 margin-top: 120px;
 margin-right: 25px;
 margin-left: 25px;
 margin-bottom: 50px;
 border-radius: 5px;
 background-color: #ffffff;
 color: #868686;
 fon-size:20px;
 span{
     display:flex;
     justify-content: center;
     align-items:center;
 }
 `
const Date = styled.p`
 color:grey;
 `
const Box = styled.div`
display: flex;
    justify-content: center;
    align-items: center;
    margin-left: -5%;
    margin-bottom:15%;
    width: 76vw;
    height: 6vh;
    font-weight:600;
    font-size: 15px;
    font-family: 'Raleway', sans-serif;
    color: #FFFFFF;
    background-color:#A328D6;
    border-radius: 5px;
    border:none;
`

const Value = styled.p`
 color: ${props => props.type === "debit" ? "#C70000" : "#03AC00"};
 `
