margin-right: 1px;
`
const ListDescription = styled.span`
color: #000000;
word-break: break-all;
max-width:40vw;
`
const ListValue = styled.span`
word-break: break-all;
max-width:40vw;
color: ${props => props.type === "debit" ? "#C70000" : "#03AC00"};
font-weight: 500;
`
